mapper.delete:
delete from CONTRAST
where COMPANY_CODE = #{companyCode}
AND PLATFORM_RANGE_CODE
=#{platformRangeCode}

    AND COMPANY_RANGE_CODE = #{companyRangeCode}

mapper.select:
SELECT count(1) FROM CONTRAST WHERE COMPANY_RANGE_CODE= #{companyRangeCode}
AND PLATFORM_RANGE_CODE = #{platformRangeCode}
AND COMPANY_CODE = #{companyCode}

SELECT
a.ID, a.COMPANY_CODE, a.PLATFORM_RANGE_CODE, a.COMPANY_RANGE_CODE, a.PLATFORM_DETAIL_CODE, a.SCORE, a.SELECTED, b.DETAIL_NAME as "platformDetailName"
FROM (SELECT ROWNUM AS rowno, t.*
FROM CONTRAST t
WHERE COMPANY_RANGE_CODE= #{companyRangeCode}
AND PLATFORM_RANGE_CODE = #{platformRangeCode}
AND COMPANY_CODE = #{companyCode}
AND ROWNUM <= #{page}*#{rows}) a
JOIN PLATFORM_RANGE_DETAIL b on b.DETAIL_CODE=a.PLATFORM_DETAIL_CODE AND b.platform_code=a.platform_range_code
WHERE a.rowno > (#{page}-1)*#{rows}
